import React, { useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Home from "./pages/Home"
import Register from "./pages/Register";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { getIsAuthenticated, setIsAuthenticated } from "./pages/Auth";

let condition = true;

const App = () =>{
    
    useEffect(()=>{
        useEffect(() => {
            // Define the function you want to run continuously
            const myFunction = () => {
                console.log('Function is running continuously!');
                // You can add more logic here
            };
    
            // Set up the interval to run the function every second (1000 ms)
            const intervalId = setInterval(myFunction, 1000);
    
            // Clear the interval on component unmount
            return () => clearInterval(intervalId);
        }, []); // Empty dependency array means this effect runs once on mount
    })
    return (
            <BrowserRouter>
                <ToastContainer/>
                <Routes>
                    <Route path="/" element={<Login />} />
                    <Route path="*" element={<Navigate to="/" />} />
                    <Route path="/register" element={<Register />} />
                    <Route element={condition ? <Home /> : <Navigate to="/" />} />
                </Routes>
            </BrowserRouter>
    )
}


export default App
