import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Home from "./pages/Home"
import Register from "./pages/Register";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Cookies from 'js-cookie';

const useCookieChange = (cookieName, callback) => {
    const [cookieValue, setCookieValue] = useState(getCookie(cookieName));

    useEffect(() => {
        const handleCookieChange = () => {
            const newValue = getCookie(cookieName);
            if (newValue !== cookieValue) {
                setCookieValue(newValue);
                callback(newValue);
            }
        };

        // Initial check
        handleCookieChange();

        // Set an interval to check for changes
        const intervalId = setInterval(handleCookieChange, 1000); // Check every second

        return () => clearInterval(intervalId); // Cleanup on unmount
    }, [cookieName, cookieValue, callback]);
};

const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
};


const App = () =>{
    let condition = "";
    
    useCookieChange('token', (newValue) => {
        console.log('Cookie changed:', newValue);
    });
    
    return (
            <BrowserRouter>
                <ToastContainer/>
                <Routes>
                    <Route path="/" element={<Login />} />
                    <Route path="*" element={<Navigate to="/" />} />
                    <Route path="/register" element={<Register />} />
                    <Route element={condition ? <Home /> : <Navigate to="/" />} />
                </Routes>
            </BrowserRouter>

            
    )
}


export default App
